package BucleDoWHile;

public class Ejemplo01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0;
		do {
			System.out.println("Iteraccion " + i);
			i++;
		}while(i<0);
		System.out.println("WHILE-------");
		i=0;
		while(i<0) {
			System.out.println("Iteraccion " + i);
			i++;
		}
	}

}
